package com.seeu.ywq.pay.service.impl;

import com.seeu.ywq.pay.exception.BalanceNotEnoughException;
import com.seeu.ywq.pay.model.Balance;
import com.seeu.ywq.pay.model.RechargeTable;
import com.seeu.ywq.pay.repository.BalanceRepository;
import com.seeu.ywq.pay.repository.RechargeTableRepository;
import com.seeu.ywq.pay.service.IosPayService;
import com.seeu.ywq.uservip.model.UserVIP;
import com.seeu.ywq.uservip.repository.UserVIPRepository;
import net.sf.json.JSONObject;
import org.apache.http.client.methods.CloseableHttpResponse;
import org.apache.http.client.methods.HttpPost;
import org.apache.http.entity.StringEntity;
import org.apache.http.impl.client.CloseableHttpClient;
import org.apache.http.impl.client.HttpClients;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.stereotype.Service;

import java.math.BigDecimal;
import java.util.Date;

@Service
public class IosPayServiceImpl implements IosPayService {

    @Autowired
    private BalanceRepository balanceRepository;
    @Autowired
    private UserVIPRepository userVIPRepository;
    @Autowired
    private RechargeTableRepository rechargeTableRepository;
    @Value("${appStore.url}")
    private String url;

    //充值钻石
    @Override
    public Balance payDiamond(Long uid, Long price) throws BalanceNotEnoughException {
        if (price == null || price <= 0) {
            throw new BalanceNotEnoughException("传入参数必须为正整数");
        }

        RechargeTable rechargeTable = rechargeTableRepository.findByPrice(BigDecimal.valueOf(price));
        Long diamonds = rechargeTable.getDiamonds();

        Balance balance = balanceRepository.findByUid(uid);
        Long param = balance.getBalance();

        balance.setBalance(param + diamonds);

        return save(balance);
    }

    @Override
    public Balance save(Balance balance) {
        return balanceRepository.save(balance);
    }


    // 充值VIP
    @Override
    public UserVIP payVip(Long uid, Long time) throws BalanceNotEnoughException {
        if (time == null || time <= 0) {
            throw new BalanceNotEnoughException("传入参数必须为正整数");
        }

        if (uid == null) return null;

        UserVIP userVIP = userVIPRepository.findOne(uid);
        Long times = time * 24 * 60 * 60 * 1000;

        // 首次充值
        if (userVIP == null) {
            UserVIP userVIP1 = new UserVIP();
            userVIP1.setUid(uid);
            userVIP1.setVipLevel(UserVIP.VIP.vip);
            Date date = new Date();
            String sum = times + date.getTime() + "";
            userVIP1.setUpdateTime(date);
            userVIP1.setTerminationDate(new Date(Long.valueOf(sum)));
            return save(userVIP1);
        } else {
            Long terDate = userVIP.getTerminationDate().getTime();
            String sum = times + terDate + "";
            userVIP.setTerminationDate(new Date(Long.valueOf(sum)));
            return save(userVIP);
        }
    }

    @Override
    public UserVIP save(UserVIP userVIP) {
        return userVIPRepository.save(userVIP);
    }

    @Override
    public String appStore() {
        CloseableHttpClient closeableHttpClient = null;
        HttpPost httpPost = null;
        JSONObject jsonObject = new JSONObject();
        jsonObject.put("receipt-data","");
        try {
            StringEntity stringEntity = new StringEntity(jsonObject.toString(), "UTF-8");
            closeableHttpClient = HttpClients.createDefault();
            httpPost = new HttpPost(url);
            httpPost.setHeader("Content-Type", "application/json");
            httpPost.setEntity(stringEntity);

            CloseableHttpResponse closeableHttpResponse = closeableHttpClient.execute(httpPost);
            System.out.println("========================"+closeableHttpResponse.getStatusLine().getStatusCode());
            return null;
        } catch (Exception e) {
            e.printStackTrace();
        }
        return null;
    }

}
